function person(){
	this.address=localStorage.getItem('address');
	this.age=localStorage.getItem('age');
	this.datetime=localStorage.getItem('datetime');
	this.education=localStorage.getItem('education');
	this.headimage=localStorage.getItem('headimage');
	this.hobby=localStorage.getItem('hobby');
	this.id=localStorage.getItem('id');
	this.integral=localStorage.getItem('integral');
	this.mail=localStorage.getItem('mail');
	this.mate=localStorage.getItem('mate');
	this.name=localStorage.getItem('name');
	this.nickname=localStorage.getItem('nickname');
	this.occupation=localStorage.getItem('occupation');
	this.passwd=localStorage.getItem('passwd');
	this.pay_passwd=localStorage.getItem('pay_passwd');
	this.phone=localStorage.getItem('phone');
	this.protocol=localStorage.getItem('protocol');
	this.referrer=localStorage.getItem('referrer');
	this.remain=localStorage.getItem('remain');
	this.sex=localStorage.getItem('sex');
	this.user_level=localStorage.getItem('user_level');
	this.uuid=localStorage.getItem('uuid');
}
